mapper.select:

select title, contents, user_no
from board
where no=#{no }

choose.otherwise:

insert
into board
select null,#{vo.title },#{vo.contents }, 0, now(), g_no, (o_no+1), (depth+1), #{vo.userNo } from board a where no=#{parentNo }

mapper.select:

select user_no
from board
where no = #{boardNo }

mapper.update:

update board
set title=#{title }, contents=#{contents }
where no=#{no }


update board
set hit=hit+1
where no=#{no }
